import React from 'react';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import FlowerUpdateForm from './FlowerUpdateForm';

class ModifyFlower extends React.Component {
    render() {
        return (
            <div>
                <Card style={{ width: '18rem' }}>
                    <Card.Img variant="top" src={this.props.item.photo} />
                    <Card.Body>
                        <Card.Title>{this.props.item.name}</Card.Title>
                        <Card.Text>
                            {this.props.item.instructions}
                        </Card.Text>
                        <Card.Text>
                            {this.props.item.email}
                        </Card.Text>
                        <Button variant="primary"
                            onClick={() => this.props.deleteFlower(this.props.item._id)}>Delete flower</Button>
                        <Button variant="primary"
                            onClick={() => this.props.showUpdateForm(this.props.item)}>Update flower</Button>
                    </Card.Body>
                </Card>
                <FlowerUpdateForm
                    show={this.state.showFlag}
                    handleClose={this.handleClose}
                    name={this.state.name}
                    instructions={this.state.instructions}
                    updateFlower={this.updateFlower}
                />
            </div>
        );
    }
}

export default ModifyFlower;
