import React from 'react';
import axios from 'axios'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import FlowerUpdateForm from './FlowerUpdateForm';

class FavFlower extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            flowersArr: [],
            show: false,
            showFlag: false,
            name: '',
            instructions: '',
            flowerId: ''
        }
    }
    componentDidMount = () => {
        const { user } = this.props.auth0
        const email = user.email
        axios
            .get(`http://localhost:3600/getFlowers?email=${email}`)
            .then(result => {
                this.setState({
                    flowersArr: result.data
                })
                console.log(this.state.flowersArr);
            })
            .catch(error => {
                console.log('error');
            })
    }
    deleteFlower = (id) => {
        const { user } = this.props.auth0
        const email = user.email
        axios
            .delete(`http://localhost:3600/deleteFlower/${id}?email=${email}`)
            .then(result => {
                this.setState({
                    flowersArr: result.data
                })
            })
            .catch(error => {
                console.log('Error');
            })
    }
    updateFlower = (selectedFlower) => {
        const { user } = this.props.auth0
        const email = user.email
        const obj = {
            name: selectedFlower.name,
            instructions: selectedFlower.instructions,
            email: email
        }
        axios
            .post(`http://localhost:3600/updateFlower/${this.state.flowerId}`, obj)
            .then(result => {
                this.setState({
                    flowersArr: result.data
                })
            })
            .catch(error => {
                console.log('error');
            })
    }

    handleClose = () => {
        this.setState({
            show: false,
            showFlag: false
        })
    }
    handleShow = () => {
        this.setState({
            show: true,
        })
    }
    showUpdateForm = (item) => {
        this.setState({
            showFlag: true,
            name: item.name,
            instructions: item.instructions,
            flowerId: item._id
        })
    }
    render() {
        return (
            <div>
                <Card style={{ width: '18rem' }}>
                    <Card.Img src={this.props.item.photo} />
                    <Card.Body>
                        <Card.Title>{this.props.item.name}</Card.Title>
                        <Card.Text>
                            {this.props.item.instructions}
                        </Card.Text>
                        <Card.Text>
                            {this.props.item.email}
                        </Card.Text>
                        <Button variant="primary"
                            onClick={() => this.props.deleteFlower(this.props.item._id)}>
                            Delete flower</Button>
                        <Button variant="primary"
                            onClick={() => this.props.showUpdateForm(this.props.item)}>
                            Update flower</Button>
                    </Card.Body>
                </Card>
                <FlowerUpdateForm
                    show={this.state.showFlag}
                    handleClose={this.handleClose}
                    name={this.state.name}
                    instructions={this.state.instructions}
                    updateFlower={this.updateFlower}
                />
            </div>
        );
    }
}

export default FavFlower;
