import React from 'react'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import './AddFlowers.css'

class FavFlowers extends React.Component {
    constructor(props)
    {
        super(props)
        this.state = {
          flowersArr: [],
          show: false,
          showFlag: false,
          name: '',
          instructions: '',
          flowerId: ''
        }
    }
    componentDidMount = () => {
        const { user } = this.props.auth0
        const email = user.email
        axios
          .get(`http://localhost:3600/getFlowers?email=${email}`)
          .then(result => {
            this.setState({
              flowersArr: result.data
            })
            console.log(this.state.flowersArr);
          })
          .catch(error => {
            console.log('error');
          })
      }
    render() {
        return (
            <div>
                {this.props.flowersArr.map((item) => {
                    return (
                        <Card style={{ width: '18rem' }} >
                            <Card.Body>
                                <Card.Img src={item.photo}/>
                                <Card.Title>{item.name}</Card.Title>
                                <Card.Text>{item.instructions}</Card.Text>
                                <Button type='submit' onClick={()=>{this.props.addFlower(item)}}>Add flower</Button>
                            </Card.Body>
                        </Card>
                    )
                })}
            </div>
        );
    }
}

export default FavFlowers;
