import React from 'react'
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'

class FavFlowers extends React.Component {

    render() {
        return (
            <div>
                {this.props.flowersArr.map((item) => {
                    return (
                        <Card style={{ width: '18rem' }} onChange={()=>{this.props.addFlower(item)}}>
                            <Card.Body>
                                <Card.Img src={item.photo}/>
                                <Card.Title>{item.name}</Card.Title>
                                <Card.Text>{item.instructions}</Card.Text>
                                <Button type='submit'>Add flower</Button>
                            </Card.Body>
                        </Card>
                    )
                })}
            </div>
        );
    }
}

export default FavFlowers;
