import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { withAuth0 } from '@auth0/auth0-react'
import axios from 'axios'
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'
import Modal from 'react-bootstrap/Modal'
import FavFlowers from './FavFlowers'

class Main extends Component {

    constructor(props)
    {
        super(props)
        {
            this.state={
                flowersArr:[],
                show:false,
                name:'',
                instructions:'',
                flowerId:''
            }
        }
    }

    componentDidMount = () => {
        const { user } = this.props.auth0
        const email = user.email
        axios
          .get(`http://localhost:3600/flowers?email=${email}`)
          .then(result => {
            this.setState({
              flowersArr: result.data
            })
          })
          .catch(error => {
            console.log('error');
          })
      }

      addFlower = (event) =>{
          event.preventDefault()
          const { user } = this.props.auth0
          const email = user.email
          const obj={
              name:event.target.name.value,
              instructions:event.target.instructions.value,
              email:email
          }
          axios
          .post('http://localhost:3600/addFlowers' ,obj)
          .then(result =>{
              this.setState({
                  flowersArr:result.data
              })
          })
          .catch(error => {
            console.log('error');
          })
      }

      deleteFlower = (id) => {
        const { user } = this.props.auth0
        const email = user.email
        axios
          .delete(`http://localhost:3600/deleteFlower/${id}?email=${email}`)
          .then(result => {
            this.setState({
              flowersArr: result.data
            })
          })
          .catch(error => {
            console.log('Error');
          })
      }
      updateFlower = (event) =>{
        event.preventDefault()
        const { user } = this.props.auth0
        const email = user.email
        const obj={
            name:event.target.name.value,
            instructions:event.target.instructions.value,
            email:email
        }
        axios
        .post(`http://localhost:3600/updateFlower/${this.state.flowerId}` ,obj)
        .then(result =>{
            this.setState({
                flowersArr:result.data
            })
        })
        .catch(error => {
          console.log('error');
        })
    }

    render() {
        return (
            <div>
                <FavFlowers/>
            </div>
        );
    }
}

export default Main;
