import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import { withAuth0 } from '@auth0/auth0-react'
import axios from 'axios'
import AddFlowers from './flower-components/AddFlowers'

class Main extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
          flowersArr: [],
          show: false,
          showFlag: false,
          name: '',
          instructions: '',
          flowerId: ''
        }
      }

    componentDidMount = () => {
        const { user } = this.props.auth0
        const email = user.email
        axios
          .get(`http://localhost:3600/flowers?email=${email}`)
          .then(result => {
            this.setState({
              flowersArr: result.data
            })
          })
          .catch(error => {
            console.log('error');
          })
      }

      addFlower = (event) =>{
          event.preventDefault()
          const { user } = this.props.auth0
          const email = user.email
          const obj={
              name:event.target.name.value,
              instructions:event.target.instructions.value,
              email:email
          }
          axios
          .post('http://localhost:3600/addFlowers' ,obj)
          .then(result =>{
              this.setState({
                  flowersArr:result.data
              })
          })
          .catch(error => {
            console.log('error');
          })
      }

      deleteFlower = (id) => {
        const { user } = this.props.auth0
        const email = user.email
        axios
          .delete(`http://localhost:3600/deleteFlower/${id}?email=${email}`)
          .then(result => {
            this.setState({
              flowersArr: result.data
            })
          })
          .catch(error => {
            console.log('Error');
          })
      }
      updateFlower = (event) =>{
        event.preventDefault()
        const { user } = this.props.auth0
        const email = user.email
        const obj={
            name:event.target.name.value,
            instructions:event.target.instructions.value,
            email:email
        }
        axios
        .post(`http://localhost:3600/updateFlower/${this.state.flowerId}` ,obj)
        .then(result =>{
            this.setState({
                flowersArr:result.data
            })
        })
        .catch(error => {
          console.log('error');
        })
    }
    handleClose = () => {
        this.setState({
          show: false,
          showFlag: false
        })
      }
      handleShow = () => {
        this.setState({
          show: true,
        })
      }
      showUpdateForm = (item) => {
        this.setState({
          showFlag: true,
          name: item.name,
          instructions: item.instructions,
          flowerId: item._id
        })
      }

    render() {
        return (
            <div>
                <AddFlowers/>
            </div>
        );
    }
}

export default withAuth0(Main);
