import React from 'react';
import axios from 'axios'
import FlowerCard from './FlowerCard'
import FlowerUpdateForm from './FlowerUpdateForm';
import { withAuth0 } from '@auth0/auth0-react'

class FavFlower extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            flowersArr: [],
            show: false,
            showFlag: false,
            name: '',
            instructions: '',
            flowerId: ''
        }
    }
    componentDidUpdate= () => {
        const { user } = this.props.auth0
        const email = user.email
       console.log('hello')
        axios
            .get(`http://localhost:3600/getFlower?email=${email}`)
            .then(result => {
                this.setState({
                    flowersArr: result.data
                })
                console.log(this.state.flowersArr);
            })
            .catch(error => {
                console.log('error');
            })
    }
    deleteFlower = (id) => {
        const { user } = this.props.auth0
        const email = user.email
        axios
            .delete(`http://localhost:3600/deleteFlower/${id}?email=${email}`)
            .then(result => {
                this.setState({
                    flowersArr: result.data
                })
            })
            .catch(error => {
                console.log('Error');
            })
    }
    updateFlower = (selectedFlower) => {
        const { user } = this.props.auth0
        const email = user.email
        const obj = {
            name: selectedFlower.name,
            instructions: selectedFlower.instructions,
            email: email
        }
        axios
            .post(`http://localhost:3600/updateFlower/${this.state.flowerId}`, obj)
            .then(result => {
                this.setState({
                    flowersArr: result.data
                })
            })
            .catch(error => {
                console.log('error');
            })
    }

    handleClose = () => {
        this.setState({
            show: false,
            showFlag: false
        })
    }
    handleShow = () => {
        this.setState({
            show: true,
        })
    }
    showUpdateForm = (item) => {
        this.setState({
            showFlag: true,
            name: item.name,
            instructions: item.instructions,
            flowerId: item._id
        })
    }
    render() {
        return (
            <div>
                <h1>Hello</h1>
               {this.state.flowersArr.map((item)=>{
               return(
                   <FlowerCard
                   item={item}
                   />
               )
               })}

                <FlowerUpdateForm
                    show={this.state.showFlag}
                    handleClose={this.handleClose}
                    name={this.state.name}
                    instructions={this.state.instructions}
                    updateFlower={this.updateFlower}
                />
            </div>
        );
    }
}

export default withAuth0(FavFlower);
